import { Box, InputBase,styled } from "@mui/material";

import React from "react";

const SearchContainer = styled(Box)`
  background: #fff;
  width:38%;
  border-radius:2px;
  margin-left:10px

`;
const InputSearchBase = styled(InputBase)`
padding-left:20px;
width:100%

`;

const Search = () => {
  return (
    <SearchContainer>
      <InputSearchBase
      
     placeholder="Search for products, brand and more" ></InputSearchBase>
    </SearchContainer>
  );
};

export default Search;
