import { Box, Button, Dialog, styled, TextField, Typography } from "@mui/material";
import React from "react";

const Component = styled(Box)`
  height: 80vh;
  width: 90vh;
`;

const LoginButton = styled(Button)`
  text-transform: none;
  background: #fb641b;
  color:#fff;
  height:48px;
  border-radius:2px
`;

const ReuestOTP = styled(Button)`
  text-transform: none;
  background: #2874f0;
  color:#fff;
  height:48px;
  border-radius:2px;
  box-shadow:0 2px 4px
`;
const Image = styled(Box)`
  background: #2874f0 url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png) center 85% no-repeat;
  height: 100%;
  width: 35%;

  padding: 45px 35px;
`;

const Wrapper = styled(Box)`
  display: flex;
  flex-direction: column;
  padding: 25px 35px;
  flex: 1;
  & > div,
  & > button,
  & > p {
    margin-top: 20px;
  }
`;

const LoginDialog = ({ open, setOpen }) => {
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <Component>
        <Box style={{ display: "flex", height: "100%" }}>
          <Image>
            <Typography variant="h5">Login</Typography>
            <Typography style={{ marginTop: "20px" }}>Get access to your orders, whishlist and recomendations</Typography>
          </Image>
          <Wrapper>
            <TextField variant="standard" label="Enter Email / Mobile Number"></TextField>
            <TextField variant="standard" label="Enter Password"></TextField>

            <Typography>By Continuing, You agree to filpkart terms of use and privacy policy.</Typography>

            <LoginButton>Login</LoginButton>
            <Typography>OR</Typography>

            <ReuestOTP>Request OTP</ReuestOTP>

            <Typography>New to Filpkart ? Create an account</Typography>
          </Wrapper>
        </Box>
      </Component>
    </Dialog>
  );
};

export default LoginDialog;
