import React from "react";

import { AppBar, Toolbar, styled, Box, Typography } from "@mui/material";
import Search from "./Search";
import CustomButtons from "./CustomButtons";

//> This StyledHeader providing the custom style to the AppBar Mui Component

const StyledHeader = styled(AppBar)`
  background: #2874f0;
  height: 55px;
`;

const Component = styled(Box)`
  margin-left: 12%;
  line-height: 0;
`;

const SubHeading = styled(Typography)`
  font-size: 10px;
  font-style: italic;
`;

const CustomButtonWrapper = styled(Box)`
margin:0 5% 0 auto:
`

//> Handlong the css for html element in mui styled
const PlusImage = styled("img")({
  width: 10,
  height:10,
  marginLeft:4
});



// Component

const Header = () => {
  const logoURL = "https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/flipkart-plus_8d85f4.png";

  const subURL = "https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/plus_aef861.png";

  return (
    <StyledHeader>
      <Toolbar style={{minHeight:"55px"}}>
        <Component>
          <img src={logoURL} alt="flipKart-logo" style={{ width: 75 }}></img>

          <Box style={{display:'flex'}}>
            <SubHeading>
              Explore&nbsp;
              <Box component="span" style={{ color: "#FFE500" }}>
                Plus
              </Box>
            </SubHeading>
            <PlusImage src={subURL} alt="sub-logo"></PlusImage>
          </Box>
        </Component>
        <Search></Search>
<Box>
    <CustomButtons></CustomButtons>
</Box>
      </Toolbar>
    </StyledHeader>
  );
};

export default Header;
